#! /usr/bin/env node

const commander = require("commander")
	.version("1.0.0")
	.option("-t, --title  [value]", "Title of the notification")
	.option("-m, --message  [value]", "Message of the notification")
	.option("-i, --icon  [value]", "Icon of the notification")
	.parse(process.argv);

require("../src/desknotify.js").notify(commander);